    private void btncadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btncadastrarActionPerformed
        String nome=this.fieldnome.getText();
         int registro= Integer.parseInt(this.fieldregistro.getText());
         int idade=Integer.parseInt(this.fieldidade.getText());
         String espec=this.fieldespec.getText();
         professor.especialidade=espec;
         professor.registro=registro;
         professor.nome=nome;
         professor.idade=idade;
         JOptionPane.showMessageDialog(null,"Dados cadastrados com sucesso!");
         this.fieldespec.setText(" ");
         this.fieldregistro.setText(" ");
         this.fieldnome.setText(" ");
         this.fieldidade.setText(" ");
    }//GEN-LAST:event_btncadastrarActionPerformed

    private void btnexibirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnexibirActionPerformed
        this.fieldnome.setText(professor.nome);
         this.fieldidade.setText(" "+professor.idade);
         this.fieldregistro.setText(" "+professor.registro);
         this.fieldespec.setText(" "+professor.especialidade);
    }//GEN-LAST:event_btnexibirActionPerformed

